package firstpackage;

public class Loopfunctions {

	public static void main(String[] args) {
		// TODO Auto-generated method stub


		
		//for loop
		//count based loop
		for(int i=1;i<=10;i++) {
			
			
			System.out.println("played pettai song");
		}
		
		
		int k=1;
		while (k<=10) {
		
			System.out.println("play master song");
			
			
		k++;	
		}
		
		
		int l=1;
		do {
			
			System.out.println("atleast one time");
		l++;	
		} while (l<=10);
		
		
		
		
		for(int j=1;j<=50; j++) {
			
			System.out.println("print numbers from 1 to 50 "+j);
		}

		
	}

}
