package firstpackage;

public class Operatorspgm {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// + - * / % < > <= >= == !=

		int x = 12;
		int y = 23;

		int z = x + y;// number
		System.out.println(z);

		System.out.println(x - y);
		System.out.println(x * y);

		System.out.println(x == y);

		// ctrl+shift+f
		
		
		//increment/decrement  ++ --
		
		int i=1;
		
		i++;//i=i+1;
		int j=++i;
		System.out.println(i);
		System.out.println(j);

	}

}
